local function ResetClickStuff()	timer.Simple(0, function()		timer.Destroy("hex_autoclicker")	end)endhook.Add("OnDisconnectFromServer", "ResetClickStuff", ResetClickStuff)local Delay		= CreateClientConVar("hex_autoclicker_clicks", 0.02, true, false)local Toggle	= falselocal function HeXAutoClicker(ply,cmd,args)		if Toggle then		Toggle = !Toggle		timer.Destroy("hex_autoclicker")		RunConsoleCommand("+attack")		timer.Simple(0.01, function()			RunConsoleCommand("-attack")		end)	else		Toggle = !Toggle		timer.Create("hex_autoclicker", Delay:GetFloat(), 0, function()			PressTheMouse()		end)	endendconcommand.Add("hex_autoclicker", HeXAutoClicker)local bool = falsefunction PressTheMouse() --FIXME	if not bool then		bool = true	else		bool = false	end	local pre = {		[true] = "+",		[false] = "-"	}	RunConsoleCommand(pre[bool].."attack")end